import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import AnnouncementItem from './AnnouncementItem';

const AnnouncementList = () => {
  const [announcements, setAnnouncements] = useState([]);
  const [totalAnnouncements, setTotalAnnouncements] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const announcementsPerPage = 5;

  useEffect(() => {
    fetchAnnouncements(currentPage);
  }, [currentPage]);
 
  const fetchAnnouncements = async (page) => {
    try {
      const response = await axios.get('http://localhost:8000/api/announcements', {
        params: { page: page + 1, pageSize: announcementsPerPage, userId: 123 }
      });

      console.log('Fetched data:', response.data);

      setAnnouncements(response.data.announcements || []);
      setTotalAnnouncements(response.data.total || 0);
    } catch (error) {
      console.error('Error fetching announcements:', error);
    }
  };

  const handlePageClick = (data) => {
    console.log('Page selected:', data.selected); // Debugging line to check click event
    setCurrentPage(data.selected);
  };

  return (
    <div>
      <ReactPaginate
        previousLabel={'Previous'}
        nextLabel={'Next'}
        breakLabel={'...'}
        pageCount={Math.ceil(totalAnnouncements / announcementsPerPage)}
        marginPagesDisplayed={2}
        pageRangeDisplayed={5}
        onPageChange={handlePageClick}
        containerClassName={'pagination'}
        activeClassName={'active'}
        previousClassName={'page-item'}
        nextClassName={'page-item'}
        pageClassName={'page-item'}
        breakClassName={'page-item'}
        previousLinkClassName={'page-link'}
        nextLinkClassName={'page-link'}
        pageLinkClassName={'page-link'}
        breakLinkClassName={'page-link'}
      />
      
      {announcements.map((announcement, index) => (
        <AnnouncementItem key={announcement.id || index} announcement={announcement} />
      ))}
    </div>
  );
};

export default AnnouncementList;
