// AnnouncementList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CustomPagination from './CustomPagination'; // Import the new CustomPagination component
import AnnouncementItem from './AnnouncementItem';

const AnnouncementList = () => {
  const [announcements, setAnnouncements] = useState([]);
  const [totalAnnouncements, setTotalAnnouncements] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const announcementsPerPage = 4; // Number of items per page

  useEffect(() => {
    fetchAnnouncements(currentPage);
  }, [currentPage]);

  const fetchAnnouncements = async (page) => {
    try {
      const response = await axios.get('http://localhost:8000/api/announcements', {
        params: { page , pageSize: announcementsPerPage, userid: 123 } // Adjusted to match API requirements
      });

      console.log('Fetched data:', response.data);

      setAnnouncements(response.data.announcements || []);
      setTotalAnnouncements(response.data.total || 0);
    } catch (error) {
      console.error('Error fetching announcements:', error);
    }
  };

  const handlePageClick = (data) => {
    console.log('Page selected:', data.selected); // Debugging line to check click event
    setCurrentPage(data.selected);
  };

  return (
    <div>
      <CustomPagination
        pageCount={Math.ceil(totalAnnouncements / announcementsPerPage)}
        onPageChange={handlePageClick} 
 
      />
      {announcements.map((announcement) => (
        <AnnouncementItem key={announcement.id  } announcement={announcement} />
      ))}
      <CustomPagination
        pageCount={Math.ceil(totalAnnouncements / announcementsPerPage)}
        onPageChange={handlePageClick}
        
      />
    </div>
  );
};

export default AnnouncementList;
